mapper.select:
		select af.*,(SELECT name FROM sys_dict where id = major_id) major from ass_award_flow_info af

		  		   and id = #{id} 
		  		   and PROC_INS_ID = #{procInsId} 
		  		   and USER_ID = #{userId} 
		  		   and OFFICE_ID = #{officeId} 
		  		   and POST = #{post} 
		  		   and AGE = #{age} 
		  		   and EDU = #{edu} 
		  		   and CONTENT = #{content} 
		  		   and OLDA = #{olda} 
		  		   and OLDB = #{oldb} 
		  		   and OLDC = #{oldc} 
		  		   and NEWA = #{newa} 
		  		   and NEWB = #{newb} 
		  		   and NEWC = #{newc} 
		  		   and ADD_NUM = #{addNum} 
		  		   and EXE_DATE = #{exeDate} 
		  		   and HR_TEXT = #{hrText} 
		  		   and LEAD_TEXT = #{leadText} 
		  		   and MAIN_LEAD_TEXT = #{mainLeadText} 
		  		   and create_by = #{createBy} 
		  		   and create_date = #{createDate} 
		  		   and update_by = #{updateBy} 
		  		   and update_date = #{updateDate} 
		  		   and remarks = #{remarks} 
		  		   and del_flag = #{delFlag} 
  	  		       and `chengguo` = #{chengguo} 
   			       and `major_id` = #{majorId}  
			       and `tuandui` = #{tuandui}  
			       and `person` = #{person}  
			       and `pro_desc` = #{proDesc}  
			       and `publish_task_id` = #{publishTaskId}  



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from ass_award_flow_info

		  		   and id = #{id} 
		  		   and PROC_INS_ID = #{procInsId} 
		  		   and USER_ID = #{userId} 
		  		   and OFFICE_ID = #{officeId} 
		  		   and POST = #{post} 
		  		   and AGE = #{age} 
		  		   and EDU = #{edu} 
		  		   and CONTENT = #{content} 
		  		   and OLDA = #{olda} 
		  		   and OLDB = #{oldb} 
		  		   and OLDC = #{oldc} 
		  		   and NEWA = #{newa} 
		  		   and NEWB = #{newb} 
		  		   and NEWC = #{newc} 
		  		   and ADD_NUM = #{addNum} 
		  		   and EXE_DATE = #{exeDate} 
		  		   and HR_TEXT = #{hrText} 
		  		   and LEAD_TEXT = #{leadText} 
		  		   and MAIN_LEAD_TEXT = #{mainLeadText} 
		  		   and create_by = #{createBy} 
		  		   and create_date = #{createDate} 
		  		   and update_by = #{updateBy} 
		  		   and update_date = #{updateDate} 
		  		   and remarks = #{remarks} 
		  		   and `chengguo` = #{chengguo} 
   			       and `major_id` = #{majorId}  
			       and `tuandui` = #{tuandui}  
