mapper.select:
		select `id`,`score_uid`,`pro_id`,`created` from ass_specialist_score_over

		  		   and id = #{id} 
		  		   and score_uid = #{scoreUid} 
		  		   and pro_id = #{proId} 
		  		   and created = #{created} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from ass_specialist_score_over

  		   and id = #{id} 
  		   and score_uid = #{scoreUid} 
  		   and pro_id = #{proId} 
  		   and created = #{created}
