mapper.select:
		select cr.`id`,`num_copies`,`type`,`pro_is_complete`,`check_is_ok`,`run_is_one_year`,`unit_is_consult_cert`,`unit_is_first_complete`,`bureau_is_first_award`,`superior_is_opionion`,`apply_rst_is_accept`,`result_is_report`,`evaluate_is_cert`,`complete_is_cert`,`bureau_is_award_cert`,`unit_is_three_year_safe`,`view_new_is_report`,`contract_is_ok`,`superior_unit_is_evaluate`,`review_is_evaluate`,`superior_review_is_evaluate`,`build_unit_is_evaluate`,`result_is_accept_cert`,`result_is_quality_eval`,`specilist_is_evaluate`,`publish_is_cert`,`high_evaluate_is_cert`,`pro_is_desc`,`apply_is_reason`,`economic_is_contrast`,`technology_is_contrast`,`new_method_is_ok`,`plan_is_drawing`,`photo_is_ok`,`need_is_vcd`,`apply_source_question`,`feedback_info`,`pro_contact`,`main_user`,`review_result`,cr.`remarks`
