mapper.select:
		select cr.`id`,`num_copies`,`unit_is_development`,`bureau_is_first`,`superior_is_opionion`,`cert_is_auth`,`popularize_is_use`,`apply_source_question`,`feedback_info`,`pro_contact`,`main_user`,`manual_is_use`,`cert_is_ok`,`popularize_is_cert`,`bureau_is_cert`,`view_new_is_report`,`review_result`
		     ,cr.`remarks`,cr.`opt_uid`,cr.`task_id`,cr.`created`,cr.`updated`,
		     pro.id pro_id,
		     pro.major,
		     pro.pro_code,
		     pro.pro_sub_type,
		     pro.pro_type,
		     pro.chengguo pro_name,
		     sd.name apply_company
		from
		     boot_do.ass_award_enterprise_project pro
                   JOIN
             sys_user su on pro.create_uid = su.user_id
                   JOIN
             sys_dept sd ON su.dept_id = sd.dept_id
		           LEFT JOIN
	     	 ass_surver_review_soft_result cr on cr.pro_id = pro.id

		  		   and id = #{id} 
		  		   and num_copies = #{numCopies} 
		  		   and unit_is_development = #{unitIsDevelopment} 
		  		   and bureau_is_first = #{bureauIsFirst} 
		  		   and superior_is_opionion = #{superiorIsOpionion} 
		  		   and cert_is_auth = #{certIsAuth} 
		  		   and popularize_is_use = #{popularizeIsUse} 
		  		   and apply_source_question = #{applySourceQuestion} 
		  		   and feedback_info = #{feedbackInfo} 
		  		   and pro_contact = #{proContact} 
		  		   and main_user = #{mainUser} 
		  		   and manual_is_use = #{manualIsUse} 
		  		   and cert_is_ok = #{certIsOk} 
		  		   and popularize_is_cert = #{popularizeIsCert} 
		  		   and bureau_is_cert = #{bureauIsCert} 
		  		   and view_new_is_report = #{viewNewIsReport} 
		  		   and review_result = #{reviewResult} 
		  		   and remarks = #{remarks} 
		  		   and opt_uid = #{optUid} 
		  		   and pro.id = #{proId} 
		  		   and pro.publish_task_id = #{taskId} 
		  		   and created = #{created} 
		  		   and updated = #{updated} 



                order by ${sort} ${order}


                order by cr.id



			limit #{offset}, #{limit}

	select count(*) from boot_do.ass_award_enterprise_project pro
	   JOIN
	sys_user su on pro.create_uid = su.user_id
	   JOIN
	sys_dept sd ON su.dept_id = sd.dept_id
   	   LEFT JOIN
	ass_surver_review_soft_result cr on cr.pro_id = pro.id

	  		   and cr.id = #{id} 
	  		   and num_copies = #{numCopies} 
	  		   and unit_is_development = #{unitIsDevelopment} 
	  		   and bureau_is_first = #{bureauIsFirst} 
	  		   and superior_is_opionion = #{superiorIsOpionion} 
	  		   and cert_is_auth = #{certIsAuth} 
	  		   and popularize_is_use = #{popularizeIsUse} 
	  		   and apply_source_question = #{applySourceQuestion} 
	  		   and feedback_info = #{feedbackInfo} 
	  		   and pro_contact = #{proContact} 
	  		   and main_user = #{mainUser} 
	  		   and manual_is_use = #{manualIsUse} 
	  		   and cert_is_ok = #{certIsOk} 
	  		   and popularize_is_cert = #{popularizeIsCert} 
	  		   and bureau_is_cert = #{bureauIsCert} 
	  		   and view_new_is_report = #{viewNewIsReport} 
	  		   and review_result = #{reviewResult} 
	  		   and remarks = #{remarks} 
	  		   and cr.opt_uid = #{optUid} 
	  		   and pro.id = #{proId} 
	  		   and pro.publish_task_id = #{taskId} 
	  		   and created = #{created} 
	  		   and updated = #{updated}
