mapper.select:
		select cr.`id`,`num_copies`,`apply_unit_is_bear`,`design_is_year`,`design_is_cert`,`unit_is_three_safe`,`bureau_is_first_award`,`superior_is_opionion`,`apply_source_question`,`feedback_info`,`pro_contact`,`main_user`,`standard_is_desc`,`department_is_approve`,`two_user_is_use`,`economic_is_cert`,`bureau_award_is_cert`,`review_result`
		     ,cr.`remarks`,cr.`opt_uid`,cr.`task_id`,cr.`created`,cr.`updated`, cr.approve_is_publish,
		     pro.id pro_id,
		     pro.major,
		     pro.pro_code,
		     pro.pro_sub_type,
		     pro.pro_type,
		     pro.chengguo pro_name,
		     sd.name apply_company
		from
		    boot_do.ass_award_enterprise_project pro
		        JOIN
		    sys_user su on pro.create_uid = su.user_id
		        JOIN
		    sys_dept sd ON su.dept_id = sd.dept_id
		        LEFT JOIN
		    ass_surver_review_standard_result cr on cr.pro_id = pro.id

		  		   and cr.id = #{id} 
		  		   and num_copies = #{numCopies} 
		  		   and apply_unit_is_bear = #{applyUnitIsBear} 
		  		   and design_is_year = #{designIsYear} 
		  		   and design_is_cert = #{designIsCert} 
		  		   and unit_is_three_safe = #{unitIsThreeSafe} 
		  		   and bureau_is_first_award = #{bureauIsFirstAward} 
		  		   and superior_is_opionion = #{superiorIsOpionion} 
		  		   and apply_source_question = #{applySourceQuestion} 
		  		   and feedback_info = #{feedbackInfo} 
		  		   and pro_contact = #{proContact} 
		  		   and main_user = #{mainUser} 
		  		   and standard_is_desc = #{standardIsDesc} 
		  		   and department_is_approve = #{departmentIsApprove} 
		  		   and two_user_is_use = #{twoUserIsUse} 
		  		   and economic_is_cert = #{economicIsCert} 
		  		   and bureau_award_is_cert = #{bureauAwardIsCert} 
		  		   and review_result = #{reviewResult} 
		  		   and remarks = #{remarks} 
		  		   and cr.opt_uid = #{optUid} 
		  		   and pro.id = #{proId} 
		  		   and pro.publish_task_id = #{taskId} 
		  		   and created = #{created} 
		  		   and updated = #{updated} 



                order by ${sort} ${order}


                order by id



			limit #{offset}, #{limit}

select count(*) from boot_do.ass_award_enterprise_project pro
       JOIN
