mapper.select:
		select cr.`id`,`num_copies`,`apply_unit_is_bear`,`bureau_is_first_award`,`superior_is_opionion`,`unit_build_is_complete_time`,`complete_is_cert`,`unit_is_three_safe`,`apply_source_question`,`feedback_info`,`pro_contact`,`main_user`,`plan_is_cert`,`groundwater_is_one_year`,`surver_is_cert`,`contract_is_cert`,`superior_is_evaluate`,`view_new_is_cert`,`technology_is_file`,`water_is_cert`,`surver_is_photo`,`surver_is_report`,`audio_is_need`,`review_result`
		     ,cr.`remarks`,cr.`opt_uid`,cr.`task_id`,cr.`created`,cr.`updated`,
		     pro.id pro_id,
		     pro.major,
		     pro.pro_code,
		     pro.pro_sub_type,
		     pro.pro_type,
		     pro.chengguo pro_name,
		     sd.name apply_company
		from
		     boot_do.ass_award_enterprise_project pro
                   JOIN
             sys_user su on pro.create_uid = su.user_id
                   JOIN
             sys_dept sd ON su.dept_id = sd.dept_id
		           LEFT JOIN
		     ass_surver_review_surver_result cr on cr.pro_id = pro.id

		  		   and cr.id = #{id} 
		  		   and num_copies = #{numCopies} 
		  		   and apply_unit_is_bear = #{applyUnitIsBear} 
		  		   and bureau_is_first_award = #{bureauIsFirstAward} 
		  		   and superior_is_opionion = #{superiorIsOpionion} 
		  		   and unit_build_is_complete_time = #{unitBuildIsCompleteTime} 
		  		   and complete_is_cert = #{completeIsCert} 
		  		   and unit_is_three_safe = #{unitIsThreeSafe} 
		  		   and apply_source_question = #{applySourceQuestion} 
		  		   and feedback_info = #{feedbackInfo} 
		  		   and pro_contact = #{proContact} 
		  		   and main_user = #{mainUser} 
		  		   and plan_is_cert = #{planIsCert} 
		  		   and groundwater_is_one_year = #{groundwaterIsOneYear} 
		  		   and surver_is_cert = #{surverIsCert} 
		  		   and contract_is_cert = #{contractIsCert} 
		  		   and superior_is_evaluate = #{superiorIsEvaluate} 
		  		   and view_new_is_cert = #{viewNewIsCert} 
		  		   and technology_is_file = #{technologyIsFile} 
		  		   and water_is_cert = #{waterIsCert} 
		  		   and surver_is_photo = #{surverIsPhoto} 
		  		   and surver_is_report = #{surverIsReport} 
		  		   and audio_is_need = #{audioIsNeed} 
		  		   and review_result = #{reviewResult} 
		  		   and cr.remarks = #{remarks} 
		  		   and cr.opt_uid = #{optUid} 
		  		   and pro.id = #{proId} 
		  		   and pro.publish_task_id = #{taskId} 
		  		   and created = #{created} 
		  		   and updated = #{updated} 



                order by ${sort} ${order}


                order by cr.id



			limit #{offset}, #{limit}

select count(*) from boot_do.ass_award_enterprise_project pro
JOIN
sys_user su on pro.create_uid = su.user_id
JOIN
sys_dept sd ON su.dept_id = sd.dept_id
LEFT JOIN
ass_surver_review_surver_result cr on cr.pro_id = pro.id

  		   and cr.id = #{id} 
