mapper.select:
		select `id`,`unit_type`,`full_unit_name`,`mailing_address_postal_code`,`contact_person_tel_number`,`name_position_number_project`,`pro_id`,`task_id`,`apply_id`,`opt_uid`,`created`,`updated`,apply_info_id from ass_oil_award_unit_info

		  		   and id = #{id} 
		  		   and unit_type = #{unitType} 
		  		   and full_unit_name = #{fullUnitName} 
		  		   and mailing_address_postal_code = #{mailingAddressPostalCode} 
		  		   and contact_person_tel_number = #{contactPersonTelNumber} 
		  		   and name_position_number_project = #{namePositionNumberProject} 
			       and apply_info_id = #{applyInfoId} 
		  		   and pro_id = #{proId} 
		  		   and task_id = #{taskId} 
		  		   and opt_uid = #{optUid} 
		  		   and created = #{created} 
		  		   and updated = #{updated} 



                order by ${sort} ${order}


                order by id asc



			limit #{offset}, #{limit}

select count(*) from ass_oil_award_unit_info

