mapper.select:
		select `id`,`comany_name`,`company_nature`,`mailing_address`,`post_code`,`email_address`,`qualification_level_scop`,`legal_representative`,`duty`,`fixed_telephone`,`mobile_phone`,`project_manager`,`email_manager`,`fixed_telephone_manager`,`mobile_phone_manager`,`contact_name`,`contact_mail`,`contact_fix_phone`,`contact_mobile`,`applicant_brief_introduction`,`pro_id`,`task_id`,`apply_id`,`opt_uid`,`created`,`updated` from ass_oil_pro_apply_info

		  		   and id = #{id} 
		  		   and comany_name = #{comanyName} 
		  		   and company_nature = #{companyNature} 
		  		   and mailing_address = #{mailingAddress} 
		  		   and post_code = #{postCode} 
		  		   and email_address = #{emailAddress} 
		  		   and qualification_level_scop = #{qualificationLevelScop} 
		  		   and legal_representative = #{legalRepresentative} 
		  		   and duty = #{duty} 
		  		   and fixed_telephone = #{fixedTelephone} 
		  		   and mobile_phone = #{mobilePhone} 
		  		   and project_manager = #{projectManager} 
		  		   and email_manager = #{emailManager} 
		  		   and fixed_telephone_manager = #{fixedTelephoneManager} 
		  		   and mobile_phone_manager = #{mobilePhoneManager} 
		  		   and contact_name = #{contactName} 
		  		   and contact_mail = #{contactMail} 
		  		   and contact_fix_phone = #{contactFixPhone} 
		  		   and contact_mobile = #{contactMobile} 
		  		   and applicant_brief_introduction = #{applicantBriefIntroduction} 
		  		   and pro_id = #{proId} 
		  		   and task_id = #{taskId} 
		  		   and opt_uid = #{optUid} 
		  		   and created = #{created} 
		  		   and updated = #{updated} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

