mapper.select:
select `id`,`apply_company`,`apply_name`,`is_three_years`,`enclosure_appraisal_book`,`is_science_award`,`is_knowledge_book`,`is_paper`,`is_apply`,`is_business_license`,`is_quality`,`validate_result`,`validate_uid`,`pro_id`,`is_last_validate`,`created`,`reject_reason`,
(SELECT pro_group_name FROM boot_do.ass_award_enterprise_project where id = vt.pro_id limit 1) proGroupName
from ass_award_style_validate_team vt where id = #{value}

		select `id`,(SELECT
		sd.name
		FROM
		boot_do.ass_award_enterprise_project pro
		JOIN
		sys_user su ON pro.create_uid = su.user_id
		JOIN
		sys_dept sd ON sd.dept_id = su.dept_id
		WHERE
		pro.id = vt.pro_id
		LIMIT 1) `apply_company`,
		(SELECT
		team_name
		FROM
		boot_do.ass_enterpri_team_info
		WHERE
		pro_id = vt.pro_id
		LIMIT 1) `apply_name`,`is_three_years`,`enclosure_appraisal_book`,`is_science_award`,`is_knowledge_book`,`is_paper`,`is_apply`,`is_business_license`,`is_quality`,`validate_result`,`validate_uid`,`pro_id`,`is_last_validate`,`created`,`reject_reason`,team_id,
		(SELECT pro_group_name FROM boot_do.ass_award_enterprise_project where id = vt.pro_id limit 1) proGroupName
		from ass_award_style_validate_team vt

		  		   and id = #{id} 
		  		   and apply_company = #{applyCompany} 
		  		   and apply_name = #{applyName} 
		  		   and is_three_years = #{isThreeYears} 
		  		   and enclosure_appraisal_book = #{enclosureAppraisalBook} 
		  		   and is_science_award = #{isScienceAward} 
		  		   and is_knowledge_book = #{isKnowledgeBook} 
		  		   and is_paper = #{isPaper} 
		  		   and is_apply = #{isApply} 
		  		   and is_business_license = #{isBusinessLicense} 
		  		   and is_quality = #{isQuality} 
		  		   and validate_result = #{validateResult} 
		  		   and validate_uid = #{validateUid} 
		  		   and pro_id = #{proId} 
		  		   and is_last_validate = #{isLastValidate} 
		  		   and created = #{created} 
			       and team_id = #{teamId} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from ass_award_style_validate_team

  		   and id = #{id} 
  		   and apply_company = #{applyCompany} 
  		   and apply_name = #{applyName} 
  		   and is_three_years = #{isThreeYears} 
  		   and enclosure_appraisal_book = #{enclosureAppraisalBook} 
  		   and is_science_award = #{isScienceAward} 
  		   and is_knowledge_book = #{isKnowledgeBook} 
  		   and is_paper = #{isPaper} 
  		   and is_apply = #{isApply} 
  		   and is_business_license = #{isBusinessLicense} 
  		   and is_quality = #{isQuality} 
  		   and validate_result = #{validateResult} 
  		   and validate_uid = #{validateUid} 
  		   and pro_id = #{proId} 
  		   and is_last_validate = #{isLastValidate} 
  		   and created = #{created}
