mapper.select:
select `dept_id`,`parent_id` parentId,`name`,`order_num` ,`del_flag` ,
register_addr ,address,enterprise_credit_code ,contacts,con_phone ,
tel_phone ,zip_code ,email,fax_number,company_register_money,company_main_opt,
company_master,real_promise,business_license  from
sys_dept

	 and dept_id = #{deptId} 
	 and parent_id = #{parentId} 
	 and name like #{name} 
	 and order_num = #{orderNum} 
	 and del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by dept_id desc



	limit #{offset}, #{limit}

select count(*) from sys_dept

	 and dept_id = #{deptId} 
	 and parent_id = #{parentId} 
	 and name like #{name} 
	 and order_num = #{orderNum} 
	 and del_flag = #{delFlag}

mapper.update:
update sys_dept
`parent_id` = #{parentId}, `name` = #{name}, `order_num` = #{orderNum},
register_addr = #{registerAddr}, address = #{address}, enterprise_credit_code = #{enterpriseCreditCode}, contacts = #{contacts}, con_phone = #{conPhone}, tel_phone = #{telPhone}, zip_code = #{zipCode}, email = #{email}, fax_number = #{faxNumber}, business_license = #{businessLicense}, `del_flag` = #{delFlag}
where dept_id = #{deptId}
