mapper.select:
		select `id`,`personal_id`,`work_start_end_time`,`work_company`,`work_position`,`work_content`,`created`,pro_id from ass_enterpri_personal_info_work_history

		  		   and id = #{id} 
		  		   and personal_id = #{personalId} 
		  		   and work_start_end_time = #{workStartEndTime} 
		  		   and work_company = #{workCompany} 
		  		   and work_position = #{workPosition} 
		  		   and work_content = #{workContent} 
		  		   and created = #{created} 
			       and pro_id = #{proId} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from ass_enterpri_personal_info_work_history

