mapper.select:
		select `id`,`team_users_record_id`,`number`,`user_name`,`age`, gender,`technical_position`,`work_company`,`research_field`,`team_work_time_len`,`user_type`,`opt_uid`,`task_id`,`created` from ass_enterpri_team_users_list

		  		   and id = #{id} 
		  		   and team_users_record_id = #{teamUsersRecordId} 
		  		   and number = #{number} 
		  		   and user_name = #{userName} 
		  		   and age = #{age} 
		  		   and technical_position = #{technicalPosition} 
		  		   and work_company = #{workCompany} 
		  		   and research_field = #{researchField} 
		  		   and team_work_time_len = #{teamWorkTimeLen} 
		  		   and user_type = #{userType} 
		  		   and opt_uid = #{optUid} 
		  		   and task_id = #{taskId} 
			       and pro_id = #{proId} 
			       and created = #{created} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

